typedef long long ll;


class BookMyShow {
public:
    vector<ll> row;
    ll seats;
    ll M;
    BookMyShow(int n, int m) {
        row.resize(n,0);
        seats = (ll)n*m;
        M = m;
    }

    vector<int> gather(int k, int maxRow) {
        for(int i = 0; i <= maxRow; i++){
            if(row[i] + k <= M){
                int ind = row[i];
                row[i] += k;
                seats -= k;
                return {i, ind};
            }
        }
        return {};
    }

    bool scatter(int k, int maxRow) {
        ll booked = accumulate(row.begin(), row.begin()+maxRow+1, 0LL);
        ll total = (ll)maxRow * M + M;
        if(total - booked < k) return false;
        seats -= k;
        for(int i = 0; i <= maxRow; i++){
            ll left = M - row[i];
            if(left <= k){
                row[i] = M;
                k -= left;
            }
            else{
                row[i] += k;
                break;
            }
        }
        return true;
    }
};
