class Solution {
    private int[][] dp;

    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        this.dp = new int[floor.length()+1][numCarpets+1];
        for(int i=0; i<floor.length(); i++){
            Arrays.fill(dp[i], -1);
        }

        char[] str = floor.toCharArray();
        return find(0, str, numCarpets, carpetLen);
    }

    // Find the minimum number of white tiles visible
    private int find(int index, char[] floor, int numCarpets, int carpetLen){
        if(numCarpets < 0) return Integer.MAX_VALUE;
        if(index >= floor.length) return 0;

        if(dp[index][numCarpets] != -1){
            return dp[index][numCarpets];
        }

        int min = Integer.MAX_VALUE;
        // If current tile is black, move to the next
        if(floor[index] == '0'){
            min = find(index+1, floor, numCarpets, carpetLen);
        }

        // If current tile is white, two options, cover the current white tile or not 
        if(floor[index] == '1'){
            min = Math.min(min, find(index + carpetLen, floor, numCarpets - 1, carpetLen));
            min = Math.min(min, 1 + find(index + 1, floor, numCarpets, carpetLen));
        }

        dp[index][numCarpets] = min;
        return min;
    }
}
