class Solution {
public:
    vector<vector<int>> dp;
    int minCover(string& s, int index, int numCarpets, int len){
        if(index >= s.length())     // if index >= s.length(), then there is no tiles present.
            return 0;
        if(dp[index][numCarpets] != -1)     // State of the dp is represented by current index and no of Carpets remaining
            return dp[index][numCarpets];
        int res1 = INT_MAX;
        if(numCarpets > 0)
            res1 = minCover(s, index + len, numCarpets-1, len);     // If carpet is still remaining, we can try covering tiles in the range [index, index + len - 1]
        int res2 = minCover(s, index+1, numCarpets, len);       // finding min no of white tiles visible if we doesn't cover the tile on this particular index
        if(s[index] == '1')                 // white tile present at index, so if we don't cover those tile, then add corresponding count to res2
            res2++;
        return dp[index][numCarpets] = min(res1, res2);   // finally we choose the minimum from the above two case
    }

    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        dp.resize(floor.length(), vector<int>(numCarpets+1, -1));
        return minCover(floor, 0, numCarpets, carpetLen);
    }
};
