class Solution {
public:
    int dp[1001][1001];
    vector<int>prefix;
    int helper(int idx,int k,string& floor,int len){
        if(idx >= floor.size())return 0;
        if(dp[idx][k] != -1)return dp[idx][k];
        int op1 = helper(idx + 1,k,floor,len);
        int op2 = 0;
        if(floor[idx] == '1' && k > 0){
            int sz = min(idx + len,(int)floor.size());
            int y = prefix[sz - 1];
            if(idx - 1 >= 0){
                y -= prefix[idx - 1];
            }
            op2 = y + helper(idx + len,k - 1,floor,len);
        }
        return dp[idx][k] = max(op1,op2);
    }
    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        int cnt = 0;
        int n = floor.size();
        memset(dp,-1,sizeof dp);
        for(int i =0;i<floor.size();i++){
            if(floor[i] == '1')cnt++;
        }
        prefix.resize(n + 1,0);
        prefix[0] = (floor[0] == '1');
        for(int i =1;i<=n;i++){
            if(floor[i] == '1'){
                prefix[i] = prefix[i - 1] + 1;
            }
            else prefix[i] = prefix[i - 1];
        }
        if(cnt == 0)return 0;
        return cnt - helper(0,numCarpets,floor,carpetLen);
    }
};
