class Solution:
    def minimumWhiteTiles(self, floor: str, numCarpets: int, carpetLen: int) -> int:
        if numCarpets*carpetLen >= len(floor):
            return 0
        l = list(map(int,list(floor)))
        dp = [[-1]*(numCarpets+1) for j in range(len(l))]

        def rec(i,n):
            nonlocal l
            if n == 0 or i >= len(l):
                return sum(l[i:])
            if dp[i][n] != -1:
                return dp[i][n]
            dp[i][n] = min(rec(i+carpetLen,n-1),rec(i+1,n)+l[i])
            return dp[i][n]

        return rec(0,numCarpets)
