    {
        // the current tile is white in color, so we have two options

        int op1=INT_MAX,op2=INT_MAX;

        // option 1 - place carpet

        if(j<k) // we can only place carpets if we have some remaining black carpets
            op1=solve(a,i+len,j+1,k,len,dp);

        // option 2 - dont place the carpet
        op2=1+solve(a,i+1,j,k,len,dp);


        return dp[i][j]=min(op1,op2);
    }

    return dp[i][j]=solve(a,i+1,j,k,len,dp);

}

