class Solution {
public:
    int solve(int i,string &s,int k,int len,vector<int> &pre,vector<vector<int>> &dp){
        int n = s.size();
        if(i>=n) return 0;

        if(dp[i][k]!=-1) return dp[i][k];

        int tk=-1e9,ksum=0;
        ksum=(i+len<=n)?(pre[i+len]-pre[i]):(pre[n]-pre[i]);

        if(k>0 and s[i]=='1') tk = ksum+solve(i+len,s,k-1,len,pre,dp);
        int nottk = 0+solve(i+1,s,k,len,pre,dp);

        return dp[i][k]=max(tk,nottk);
    }
    int minimumWhiteTiles(string s, int numCarpets, int carpetLen) {
        int n = s.size();
        vector<int> pre(n+1,0);
        vector<vector<int>> dp(n+1,vector<int>(numCarpets+1,-1));
        for(int i=1;i<=n;i++) {
            pre[i]=pre[i-1];
            if(s[i-1]=='1') pre[i]+=1;
        }

        return pre[n]-solve(0,s,numCarpets,carpetLen,pre,dp);
    }
};
