class Solution {
public:
//returns the min no. of white carpets ;
    int f(int i, int k, int kl, string &s, vector<vector<int>> & dp){
        //base cases
        if(i < 0)return 0;
        if(k == 0){
            //return all the white tiles in the string s[0...i];
            int ones = 0;
            for(int ii=0; ii<=i; ii++){
                ones += (s[ii]=='1');
            }
            return ones;
        }
        if(dp[i][k] != -1)return dp[i][k];
        int ans = INT_MAX;;
        //we ignore;
        ans = f(i-1, k, kl, s, dp) + (s[i] == '1');
        //we cover it;
        ans = min(ans, f(i-kl, k-1, kl, s, dp));

        return dp[i][k] = ans;
    }

    int minimumWhiteTiles(string s, int k, int kl) {
        int n = s.length();
        vector<vector<int>> dp(n+1,vector<int>(k+1, -1));
        return f(n-1, k, kl, s, dp);
    }
};
