class Solution {

    int solve(int idx,vector<int> &arr,int k,int n,vector<vector<int>> &dp){
        if(n==0 || idx>=arr.size()){
            return 0;
        }

        if(dp[idx][n]!=-1)return dp[idx][n];

        // use 
        int use;
        if(idx==0)use=arr[idx+(k-1)] + solve(idx+k,arr,k,n-1,dp);
        else if(idx+k>=arr.size()) use=arr.back()-arr[idx-1] + solve(idx+k,arr,k,n-1,dp);
        else use = use=arr[idx+(k-1)]-arr[idx-1] + solve(idx+k,arr,k,n-1,dp);

        // skip
        int skip=solve(idx+1,arr,k,n,dp);

        return dp[idx][n]=max(use,skip);

    }
public:
    int minimumWhiteTiles(string floor, int n, int k) {
        int cnt=0;
        vector<int> arr;
        int sz=floor.length();
        vector<vector<int>> dp(sz+1,vector<int>(n+1,-1));
        for(auto i:floor){
            if(i=='1')cnt++;
        arr.push_back(cnt);
        }

        return cnt-solve(0,arr,k,n,dp);

    }
};
```

