func minimumWhiteTiles(s string, nc int, cl int) int {
	ps := make([]int, 0, len(s))
	c := 0
	for i := 0; i < len(s); i++ {
		if s[i] == 49 {
			c++
		}
		ps = append(ps, c)
	}

	mem := make([][]int, len(s))
	for i := 0; i < len(s); i++ {
		mem[i] = make([]int, nc+1)
		for j := 0; j < nc+1; j++ {
			mem[i][j] = -1
		}
	}

	var dp func(p, oc int) int
	dp = func(p, oc int) int {
		if p >= len(s) {
			return 0
		}
		if oc <= 0 {
			return ps[len(ps)-1] - ps[p-1]
		}
		if mem[p][oc] != -1 {
			return mem[p][oc]
		}
		mem[p][oc] = 0
		if s[p] == 49 {
			mem[p][oc] += 1 + dp(p+1, oc)
		} else {
			mem[p][oc] += dp(p+1, oc)
		}
		k := dp(p+cl, oc-1)
		if k < mem[p][oc] {
			mem[p][oc] = k
		}
		return mem[p][oc]
	}
	return dp(0, nc)
}
