var minimumWhiteTiles = function(floor, numCarpets, carpetLen) {
    let prev = Array(floor.length + 1).fill(0);

    for (let i = 1; i <= floor.length; i++)
        prev[i] = prev[i - 1] + (floor[i - 1] == "1");

    for (let j = 1; j <= numCarpets; j++) {
        const cur = Array(floor.length + 1).fill(0);

        for (let i = 1; i <= floor.length; i++)
            cur[i] = Math.min(cur[i - 1] + (floor[i - 1] == "1"), 
                i >= carpetLen ? prev[i - carpetLen] : 0);

        prev = cur;
    }
    return prev.at(-1);
};
```
```
var minimumWhiteTiles = function(floor, numCarpets, carpetLen) {
    const dp = Array.from({ length: floor.length }, 
        () => Array(numCarpets + 1).fill(Infinity));

    for (let i = floor.length - 1; i >= 0; i--)
        dp[i][0] = (i + 1 < floor.length && dp[i + 1][0]) + (floor[i] == "1");

    const recursion = (index, numCarpets) => {
        if (index >= floor.length) return 0;
        if (dp[index][numCarpets] < Infinity) return dp[index][numCarpets];

        return dp[index][numCarpets] = Math.min(
            (floor[index] == "1") + recursion(index + 1, numCarpets), 
            recursion(index + carpetLen, numCarpets - 1)
        );
    };

    return recursion(0, numCarpets);
};
```

