class Solution {
    int dp[1001][1001];
    vector<int> suffix;
    int utilfunc(int i,int j,string &floor,int len)
    {
        if(i >= floor.length())
        {
            return 0;
        }

        if(j == 0)
        {
            return suffix[i];
        }

        if(dp[i][j] != -1)
        {
            return dp[i][j];
        }

        int ans1 = utilfunc(i+1,j,floor,len) + (floor[i] == '1');
        int ans2 = utilfunc(i+len,j-1,floor,len);
        return dp[i][j] = min(ans1,ans2);
