        memset(dp,-1,sizeof(dp));
        int n  = floor.size();
        suffix.resize(n,0);
        suffix[n-1] = floor[n-1] == '1';
        for(int i=n-2;i>=0;i--)
        {
            suffix[i] += suffix[i+1] + (floor[i] == '1');
        }

        return utilfunc(0,num,floor,len);
    }
};
