def minimumWhiteTiles(self, floor, numCarpets, carpetLen):
    dp = functools.cache(
        lambda i, j: 1000 if i < 0 else 0 if j < 0
        else min(dp(i - 1, j - carpetLen), dp(i, j - 1) + int(floor[j]))
    )
    return dp(numCarpets, len(floor) - 1)
