class Solution {
    public int f(char[] arr,int i,int n,int len,int[] whites,int dp[][])
    {
        if(i>=arr.length||n==0)//outofbounds
        {
            return 0;
        }
        if(dp[i][n]!=-1)
        {
            return dp[i][n];
        }
        if(arr[i]=='0')//black
        {
            return dp[i][n]=f(arr,i+1,n,len,whites,dp);//skip
        }
        //boundary check
        int l=Math.min(i+len,arr.length)-1;//if white,get the min index after add carpte's len
        int white=whites[l];
        if(i!=0)
        {
            white-=whites[i-1];//if i==0 no need to diff for white tiles
        }
        int pick=white+f(arr,i+len,n-1,len,whites,dp);// either pick ->white tile+after effect of putting carpet
        int nopick=f(arr,i+1,n,len,whites,dp);// not picking skipping
        return dp[i][n]=Math.max(pick,nopick);//getting max of both
    }
    public int minimumWhiteTiles(String floor, int n, int len) {
        int whites[]=new int[floor.length()];//prefix sum of whites
        int dp[][]=new int[floor.length()+1][n+1];
        for(int i=0;i<dp.length;i++)
        {
            for(int j=0;j<dp[0].length;j++)
            {
                dp[i][j]=-1;   
            }
        }
        char arr[]=floor.toCharArray();
        whites[0]=(arr[0]=='1'?1:0);// prefix sum of whites 0th ele may or maynot be white in case..
        for(int i=1;i<arr.length;i++)
        {
            whites[i]=whites[i-1]+(arr[i]=='1'?1:0);//prefix sum
        }
        if(whites[floor.length()-1]==0)// after prefix sum the last ele is 0 then no whites at all return 0 simply
        {
            return 0;
        }
        return whites[floor.length()-1]-f(arr,0,n,len,whites,dp);// no.of whites required-> so total whites-used white tiles
    }
}
