                        dp[i][j] = (i - carpetLen < 0) ? 0 : dp[i-carpetLen][j-1];
                        dp[i][j] = min(dp[i][j], 1 + dp[i-1][j]);
                    }
                }
            }
        }
        return dp[n][numCarpets];
    }
};
