    def minimumWhiteTiles(self, floor: str, numCarpets: int, carpetLen: int) -> int:
        n = len(floor)

		#prefix sum of white tiles till now
        f = [0] * (n+1)
        for i in range(1, n+1):
            f[i] += f[i-1]
            if floor[i-1] == '1':
                f[i] += 1

		#dp over each index for different no. of tiles
        dp = [[0]*(numCarpets+1) for _ in range(n+1)]
        for i in range(1, n+1):
            for j in range(1,numCarpets+1):
                prevIdx = max(0, i-carpetLen)
                newCoverage = f[i]-f[prevIdx] + dp[prevIdx][j-1]
                dp[i][j] = max(dp[i-1][j], newCoverage)

        return f[n] - dp[n][numCarpets]

