class Solution {
    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        int len = floor.length();
        int[][] dp = new int[numCarpets + 1][len+1];
        int[] prefix = new int[len];
        int tiles = 0, total = 0;

        for(int i = 0; i < len; i++)
        {
            tiles += floor.charAt(i) - '0'; // calculate total no of Tiles within the Carpet Length Window
            if(i - carpetLen >= 0) // start excluding tiles which are not in the Range anymore of the Carpet Length given
                tiles -= floor.charAt(i - carpetLen) - '0'; 
            prefix[i] = tiles; // the total no of tiles covered within the Carpet Length range for current index
            total += floor.charAt(i) - '0';
        }

        for(int i = 1; i <= numCarpets; i++)
        { 
            for(int j = 0; j < len; j++)
            {
                int doNot = dp[i][j]; // if we do not wish to cover current Tile
                int doTake = dp[i-1][Math.max(0, j - carpetLen + 1)] + prefix[j]; // if we do wish to cover current tile
                // we should go back the Carpet length & check for tiles not covered before j - carpet Length distance
                dp[i][j + 1] = Math.max(doTake, doNot);
            }
        }

        return total - dp[numCarpets][len];
    }
}
