class Solution {
public:

    int clen;

    vector<int> prefix;

    int dp[1001][1001];

    int helper(string floor, int idx, int numc){

        //cout << idx << " " << numc << endl;

        int n = floor.size();

        if(numc == 0 || idx >= n) return 0;

        if(dp[idx][numc] != -1) return dp[idx][numc];

        if(floor[idx] == '0') return dp[idx][numc] = helper(floor, idx+1, numc);

        int res = INT_MIN;

        int tillIdxCover = min(n, idx+clen);
        int tilesCovered = prefix[tillIdxCover-1] - prefix[idx]+1;

        res = max(res, tilesCovered +helper(floor, tillIdxCover, numc-1));

        if(tilesCovered == clen) return dp[idx][numc] = res;

        res = max(res, helper(floor, idx+1, numc));

        return dp[idx][numc] = res;

    }

    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {

        int n = floor.size();

        memset(dp, -1, sizeof(dp));

        clen = carpetLen;

        int cnt = 0;

        for(int i=0;i<floor.size();i++){
            if(floor[i] == '1') cnt++;
            prefix.push_back(cnt);
        }

        if(cnt == 0) return 0;

        return cnt-helper(floor, 0, numCarpets);

    }
};
