        vector<int> floor(carpetLen * (s.size() / carpetLen + (s.size() % carpetLen != 0)) + 1);
        for (int i = 1; i < floor.size(); ++i) {
            floor[i] = floor[i - 1] + (i <= s.size() ? s[i - 1] - '0' : 0);
