int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        int n=floor.size();
        vector<vector<int>> dp(n+1,vector<int>(numCarpets+1,0));
        //Initialising
        for(int i=0;i<n;i++){
            if(i!=0)
            dp[i][0]=dp[i-1][0];
            if(floor[i]=='1'){
                dp[i][0]+=1;
            }
        }
        for (int j=1; j<=numCarpets;j++){ 
            for(int i=0;i<n;i++){
                int skipcarpet;
                if(i==0)
                    skipcarpet=floor[i]=='1' ? 1 :0;
                else
                skipcarpet=floor[i]=='1' ? dp[i-1][j]+1 :dp[i-1][j];
                if(i-carpetLen>=0)
                dp[i][j]=min(dp[i-carpetLen][j-1], skipcarpet);  // min(usecarpet,skipcarpet)
                else 
                 dp[i][j]=min(0,skipcarpet);
            }
        }

        return dp[n-1][numCarpets];
    }
