class Solution {
  func minimumWhiteTiles(_ floor: String, _ numCarpets: Int, _ carpetLen: Int) -> Int {
    let arr = Array(floor)
    var memo: [String: Int] = [:]
    func aux(_ index: Int, _ remains: Int) -> Int {
      let key = "\(index)-\(remains)"
      if memo[key] != nil {
        return memo[key]!
      }
      if remains < 0 {
        return Int.max
      }
      if index >= floor.count || remains * carpetLen >= floor.count - index {
        return 0
      }
      if arr[index] == "0" {
        memo[key] = aux(index + 1, remains)
        return memo[key]!
      }
      memo[key] = min(
        aux(index + carpetLen, remains - 1),
        1 + aux(index + 1, remains)
      )
      return memo[key]!
    }
     return aux(0, numCarpets)
  }
}
