use std::cmp::min;
use std::cmp::max;
type VI = Vec<i32>;
impl Solution {
    pub fn minimum_white_tiles(A: String, M: i32, K: i32) -> i32 {
        let N = A.len();
        let mut S = vec![0; N + 1];
        for i in (0..N).rev() {
            S[i] = if A.chars().nth(i).unwrap() == '1' { 1 } else { 0 } + S[i + 1];
        }
        struct Go<'a> { f: &'a dyn Fn(&Go, usize, i32) -> i32 }
        let go = Go {
            f: &|go, i, have| {
                if i == N || have == 0 {
                    return 0;
                }
                let j = min(i + K as usize, N);
                let include = S[i] - S[j] + (go.f)(go, j, have - 1);
                let exclude = (go.f)(go, i + 1, have);
                return max(include, exclude);
            }
        };
        return S[0] - S[N] - (go.f)(&go, 0, M);
    }
}
