```class Solution {
public:
    int minimumWhiteTiles(string floor, int num, int carpetlen) {
      //knapsack type problem
      int n = floor.length() ;
      int dp[n+1][num+1] ;
      memset(dp,0,sizeof(dp)) ;
      for(int i=n-1;i>=0;i--)
      {
          for(int j=0;j<=num;j++)
          {
