        if(str[pos] == '1')
        {
            op1 = solve(str, n - 1, pos + carpetLen, carpetLen, cnt); // place the carpet

            op2 = 1 + solve(str, n, pos + 1, carpetLen, cnt); // did not place the carpet, so the white tile is still visisble
