class Solution {
    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        int ded = 0;
        int[][] dp = new int[numCarpets+1][floor.length()];
        for(int i = 0; i < floor.length(); i++) {
            if(floor.charAt(i) == '1') ded++;
        }
        if(ded == floor.length()){
            return ded - numCarpets*carpetLen>=0?ded-numCarpets*carpetLen:0;
        }

        if(ded == 0){
            return 0;
        }

        return ded - helper(floor, numCarpets, carpetLen, 0, dp);
    }

    private int helper(String floor, int numCarpets, int carpetLen, int start, int[][] dp){
        if(start >= floor.length() || numCarpets==0) return 0;

        if(dp[numCarpets][start]!=0) return dp[numCarpets][start];

        int take= calc(floor, start,  start + carpetLen);
        if(floor.charAt(start) == '1') {
            return dp[numCarpets][start] = Math.max((take + helper(floor, numCarpets-1, carpetLen, start+carpetLen, dp)),
                           helper(floor, numCarpets, carpetLen, start+1, dp));
        }else if(floor.charAt(start) == '0'){
            return dp[numCarpets][start] = helper(floor, numCarpets, carpetLen, start+1, dp);
        }
        return dp[numCarpets][start];
    }

    private int calc(String floor, int start, int end) {
        int answer = 0;
        for(int i = start; i < end && i < floor.length() ; i++) {
            if(floor.charAt(i) == '1') answer++;
        }
        return answer;
    }
}
