solutions.content:
* Solve function will return the maximum number of white tiles that can be covered with carpets 
* Therefore , Minimum Visible white tiles = Total white tiles - Value returned by Solve function

```
class Solution {
public:
    int dp[1001][1001];
    //returns the max number of white tiles that can be covered
    int solve(string &floor , int i , int carpets ,int len,vector<int>&suff ){
        if(i >= floor.size() || !carpets ) return 0 ;
        if(dp[i][carpets] != -1) return dp[i][carpets] ;

		//at every index i we have two options -> lay the carpet(if tile is white) , or dont lay and move ahead
        int Laid = 0 , notLaid = 0 , covered = 0 ;
        //if the current tile is white only then lay the carpet
        if(floor[i] == '1'){
          covered = (i+len-1 >= floor.size() ? suff[suff.size()-1] : suff[i+len-1]) - (i-1>=0 ? suff[i-1] : 0 ) ;
          Laid = covered + solve(floor,i+len,carpets-1,len,suff) ;  
        } 
        notLaid = solve(floor,i+1,carpets,len,suff) ;
        return dp[i][carpets] = max(Laid,notLaid) ;
    }
    int minimumWhiteTiles(string &floor, int carpets, int len ) {

        vector<int>suff(floor.size(),0) ;
        //make suffix array  for O(1) access to number of white tiles between given range 
        for(int i = 0 ; i < floor.size() ; ++i ){
            if(floor[i] == '1') ++suff[i];
            if(i!=0) suff[i] += suff[i-1] ;
        }

        memset(dp,-1,sizeof(dp)) ;
        return suff[suff.size()-1] - solve(floor,0,carpets,len,suff) ; 
    }
};
```

