class Solution {
public:
    int findClosestNumber(vector<int>& nums) {
        vector<int> pos;
        vector<int> neg;
        for(auto x: nums){
            if(x>0){
                pos.push_back(x);
            }
            else{
                neg.push_back(x);
            }
            if(x==0){
                return 0;
            }
        }
        if(pos.size()!=0){
            sort(pos.begin(),pos.end());
        }
        if(neg.size()!=0){
            sort(neg.begin(),neg.end());
        }
        int n=neg.size();
        if(pos.size()==0){
            return neg[n-1]; 
        }
        if(neg.size()==0){
            return pos[0];
        }
        return abs(neg[n-1])<pos[0] ? neg[n-1]:pos[0];
    }
};
```

