    // O(n) time | O(1) space
    func findClosestNumber(_ nums: [Int]) -> Int {
        var closest = (0, Int.max) // (actualNumber, difference)

        for num in nums {
            let abs = abs(num)

            if abs < closest.1 {
                closest = (num, abs)
            } else if abs == closest.1 {
                closest = (max(num, closest.0), abs)
            }
        }

        return closest.0
    }
}
