class Solution:
    def findClosestNumber(self, nums: List[int]) -> int:
        closest = set()
        cur_min = 10 ** 5 + 1
        for num in nums:
            if num < 0:
                tmp = -num
            else:
                tmp = num
            if tmp < cur_min:
                closest.clear()
                closest.add(num)
                cur_min = tmp
            elif tmp == cur_min:
                closest.add(num)
        return max(closest)
