class Solution {

    public int findClosestNumber(int[] nums) {

        Pair pair = new Pair(Integer.MAX_VALUE, Integer.MAX_VALUE);

        for (int i=0; i<nums.length; i++) {

            int currDistance = Math.abs(nums[i]) - 0;

            if (pair.distance > currDistance) {

                pair.num = nums[i];
                pair.distance = currDistance;

            } else if (pair.distance == currDistance) {

                if (pair.num < nums[i]) {
                    pair.num = nums[i];
                }
            }
        }

        return pair.num;
    }

    private class Pair {

        int num;
        int distance;

        public Pair(int num, int distance) {

            this.distance = distance;
            this.num = num;
        }
    }
}
