class Solution:
    def countLatticePoints(self, circles: List[List[int]]) -> int:
        circle=[]
        for i in circles:
            if i not in circle:
                circle.append(i)
        dic={}
        visited={}
        def fun(i,x,y):
            if (x,y) in visited:
                return 0
            if (i[0]-x)**2+(i[1]-y)**2<=i[2]**2 and (x,y) not in dic:
                dic[x,y]=1
                visited[x,y]=1
                return 1+fun(i,x+1,y)+fun(i,x-1,y)+fun(i,x,y+1)+fun(i,x,y-1)
            if (i[0]-x)**2+(i[1]-y)**2<=i[2]**2 and (x,y) not in visited:
                visited[x,y]=1
                return fun(i,x+1,y)+fun(i,x-1,y)+fun(i,x,y+1)+fun(i,x,y-1)
            return 0
        ans=0
        for i in (circle):
            ans+=fun(i,i[0],i[1])
            visited={}
        return ans


