class Solution {
    public int countLatticePoints(int[][] circles) {

        Set<String> hashSet = new HashSet<>();

        for(int i=0; i<=circles.length-1;i++){
            int xCenter = circles[i][0];
            int yCenter = circles[i][1];
            int r  = circles[i][2];

            for(int x=xCenter-r; x<=xCenter+r; x++){
                for(int y=yCenter-r; y<=yCenter+r; y++){
                    if(isPointInsideCircle(x, y, xCenter, yCenter, r)){
                        hashSet.add(x+","+y);
                    }
                }
            }
        }

        return hashSet.size();

    }

    public boolean isPointInsideCircle(int x, int y, int xc, int yc, int r){
        int distance = (x - xc) * (x - xc) + (y - yc) * (y - yc);
        return distance <= r * r;
    }
