class Solution {
public:
    int countLatticePoints(vector<vector<int>>& circles) {

        vector<vector<int>> grid(401, vector<int>(401, 0));
        int center_r = 200;
        int center_c = 200;
        int count = 0;
        for(int i = 0; i < circles.size(); i++){
            circles[i][0] += center_r;
            circles[i][1] += center_c;
            for(int j = circles[i][0]-circles[i][2]; j <= circles[i][0]+ circles[i][2]; j++){
                for(int k = circles[i][1] - circles[i][2]; k <= circles[i][1]+ circles[i][2]; k++){
                    if(j < 0 || k < 0 || j >= grid.size() || k >= grid[0].size()) continue;
                    if(grid[j][k] == 1) continue;
                    if(inCircle(circles[i], {j, k})){
                        grid[j][k]++;
                        count++;
                    }
                }
            }
        }
        return count;
    }

private:
        bool inCircle(vector<int>& circle, vector<int> point){
            double distance = sqrt((circle[0]-point[0]) * (circle[0]-point[0]) + (circle[1]-point[1]) * (circle[1]-point[1]));
            if(distance <= (circle[2] + 0.00001)) return true;
            return false;
        }
};
