            yMin = Math.min(yMin,c[1]-c[2]);
            yMax = Math.max(yMax,c[1]+c[2]);
        }

        int ans = 0;

        for(int x=xMin; x<=xMax; x++){
            for(int y=yMin; y<=yMax; y++){
                for(int[] c:circles){
                    if((c[0]-x)*(c[0]-x)+(c[1]-y)*(c[1]-y)<= c[2]*c[2]){ // Inside circle or not?
                        ans++;
                        break;
                    }
                }
            }
        }
        return ans;
}
