use std::collections::HashSet;

impl Solution {
    pub fn count_lattice_points(circles: Vec<Vec<i32>>) -> i32 {
        let mut lattice_points: HashSet<(i32, i32)> = HashSet::new();

        //let mut exclusive_max: i32 = 0;

        for circle in circles.iter() {
          let radius: i32 = circle[2];
          let center: (i32, i32) = (circle[0], circle[1]);

          // For each integer row in the top half of the circle,
          // excluding the top row which will always be a single point
          lattice_points.insert((center.0, center.1+radius));

          //println!("circle: {:?}", circle);
          //println!("  top point: {:?}", (center.0, center.1+radius));

          // and the center row which has no "bottom reflection",
          for height_from_center in (0..radius).rev() {

            //top row
            let y1: i32 = center.1 + height_from_center;
            //bottom reflection
            let y2: i32 = center.1 - height_from_center;


            // Calculate where the arc of the circle crosses that row:
            // distance ^2 = delta_x ^2 + delta_y ^2
            // distance here is radius (the circle arc)
            // delta_y is the row we're on minus the radius
            // delta_x will likely be a non-integer value, 
            // so we take the floor to get the x-distance from the
            // center to the furthest interior integer coordinate on that row

            let x_distance: i32 = ((radius * radius - height_from_center * height_from_center) as f64).sqrt() as i32;
            // and this distance can be used to calculate 
            // the inclusive boundary lattice points at either end

            //print!("    d_x: {} ... adding: ", x_distance);

            for x in (center.0-x_distance..=center.0+x_distance) {
              lattice_points.insert((x, y1));
              lattice_points.insert((x, y2));
              //print!(" top {:?} bot {:?} ", (x, y1), (x, y2));
            }

          }
          //println!("");
          //print!("   adding center: ");
          //Add the center row
          for center_row_x in (center.0-radius..=center.0+radius) {
            lattice_points.insert((center_row_x, center.1));
            //print!(" {:?}", (center_row_x, center.1));
          }
          //println!("");
          //Add the bottom lattice point

          lattice_points.insert((center.0, center.1-radius));
          //println!("  adding bottom point: {:?}", (center.0, center.1-radius));

          //println!("--circle complete: now total {} unique lattice points", lattice_points.len());
      }

      lattice_points.len() as i32

  }
}

