class Solution {
    fun countLatticePoints(A: Array<IntArray>): Int {
        var seen = mutableSetOf<String>()
        fun dist(i: Int, j: Int, u: Int, v: Int): Double {
            var x = Math.abs(i - u).toDouble()
            var y = Math.abs(j - v).toDouble()
            return Math.sqrt(x * x + y * y)
        }
        for ((i, j, r) in A)
            for (u in i - r..i + r)
                for (v in j - r..j + r)
                    if (dist(i, j, u, v) <= r)
                        seen.add("$u,$v")
        return seen.size
    }
}
