    pub fn count_lattice_points(A: VVI) -> i32 {
        let mut seen = HashSet::new();
        fn dist(i: i32, j: i32, u: i32, v: i32) -> f32 {
            let x = (i - u).abs() as f32;
            let y = (j - v).abs() as f32;
            return (x * x + y * y).sqrt();
        }
        for row in A {
            let (i, j, r) = (row[0], row[1], row[2]);
            for u in i - r..=i + r {
                for v in j - r..=j + r {
                    if dist(i, j, u, v) <= r as f32 {
                        seen.insert(format!("{}, {}", u, v));
                    }
                }
            }
        }
        return seen.len() as i32;
    }
}
