class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    using Set = unordered_set<string>;
    int countLatticePoints(VVI& A, Set seen = {}) {
        auto dist = [](auto i, auto j, auto u, auto v) {
            auto x = abs(i - u),
                 y = abs(j - v);
            return sqrt(x * x + y * y);
        };
        auto key = [](auto u, auto v) {
            stringstream ss; ss << u << "," << v;
            return ss.str();
        };
        for (auto& row: A) {
            auto [i, j, r] = tie(row[0], row[1], row[2]);
            for (auto u{ i - r }; u <= i + r; ++u)
                for (auto v{ j - r }; v <= j + r; ++v)
                    if (dist(i, j, u, v) <= r)
                        seen.insert(key(u, v));
        }
        return seen.size();
    }
};
