	class Solution {
		public int countLatticePoints(int[][] circles) {
			int[] dx = {1,-1,0,0};
			int[] dy = {0,0,1,-1};
			Set<Pair<Integer, Integer>> points = new HashSet<>();
			Queue<Pair<Integer, Integer>> q = new ArrayDeque<>();
			for(int[] circle: circles){
				q.offer(new Pair<>(circle[0], circle[1]));
				points.add(new Pair<>(circle[0], circle[1]));
			}

			while(!q.isEmpty()){
				Pair<Integer, Integer> cur = q.poll();
				for(int i = 0; i < 4; i++){
					Pair<Integer, Integer> next = new Pair<Integer, Integer>(cur.getKey() + dx[i], cur.getValue() + dy[i]);
					if(!points.contains(next) && isValid(circles, next)){
						q.offer(next);
						points.add(next);
					}
				}
			}

			return points.size();
		}


		private boolean isValid(int[][] circles, Pair<Integer, Integer> point){
			for(int[] circle: circles){
				int ox = circle[0], oy = circle[1], r = circle[2], x = point.getKey(), y = point.getValue();
				if(Math.pow(x - ox, 2) + Math.pow(y - oy, 2) <= Math.pow(r, 2)){
					return true;
				}
			}
			return false;
		}
	}

