```
public class Solution {
    public int CountLatticePoints(int[][] circles) 
    {
        int ret = 0; 

        // store candidate points in a manner that allows fast search afterwards,
        // to avoid counting the same point twice
        // KEY = the X coordinate and VALUE = all Y coordinates of the points
        // having the same X coordinate
        var points = new Dictionary<int, HashSet<int>>();

        var n = circles.Length;

        // iterate all circles
        for(int c = 0; c < n; c++)
        {
            // extract the center (cx, cy) and radius (r) values
            var cx = circles[c][0];
            var cy = circles[c][1];
            var r = circles[c][2];
            var rr = r * r;

            // iterate the 2D grid with a 1 unit step (ensures lattice points)
            // limited to the circumscribed square of the circle  
            var startX = cx - r;
            var endX = cx + r;
            var startY = cy - r;
            var endY = cy + r;

            for(int x = startX; x <= endX; x++)
                for(int y = startY; y <= endY; y++)
                    // check whether the current (x, y) point is inside the circle
                    // meaning that the distance from the circle's center to that point
                    // is less or equal to that circle's radius
                    if (Math.Pow(cx -x, 2) + Math.Pow(cy - y, 2) <= rr)
                    {            
                        // if the current (x, y) point is a valid candidate,
                        // check whether it wasn't already stored/counted
                        // if not, store it and increment the result    
                        if (!points.ContainsKey(x))
                        {
                            // add a brand new (x, y) point
                            points[x] = new HashSet<int> { y };
                            ret++;
                        }
                        else
                            if (!points[x].Contains(y))
                            {
                                // add a new Y coordinate to the list of points 
                                // having the same X coordinate   
                                points[x].Add(y);
                                ret++;
                            }
                    }
        }

        return ret;
    }
}
```

