class Solution {

    public class Pair{
        int x;
        int y;
        public Pair(int xi,int yi){
            x=xi;
            y=yi;
        }
    }

    public boolean checkDistance(Pair point,Pair origin,int r){
        double dist = Math.sqrt(Math.pow(point.x-origin.x,2) + Math.pow(point.y-origin.y,2));
        return dist <= (double)r;
    }

    public int countLatticePoints(int[][] circles) {
        Set<String> lpoints=new HashSet<String>();
        int n=circles.length;
        for(int k=0;k<n;k++){
            Pair origin = new Pair(circles[k][0],circles[k][1]);
            int r=circles[k][2];
            Pair lbound = new Pair(origin.x-r,origin.y-r);
            Pair ubound = new Pair(origin.x+r,origin.y+r);
            for(int i=lbound.x;i<=ubound.x;i++){
                for(int j=lbound.y;j<=ubound.y;j++){
                    if(checkDistance(new Pair(i,j),origin,r)){
                        lpoints.add(i+" "+j);
                    }
                }
            }

        }
        return lpoints.size();
    }
}

