        int distanceSqr = xDiff * xDiff + yDiff * yDiff;
        return distanceSqr <= circleRadius * circleRadius;
   }
   void helper(const vector<int> &circleCordinates,int tempXPoint,int tempYPoint,int downLimit,int upLimit) {
       //base condition out of bounds up and down
       if ( (tempYPoint >= downLimit && tempYPoint<= upLimit) == false) {
           return ;
       }
       if (isPointInsideTheCircle(circleCordinates[0],tempXPoint,circleCordinates[1],tempYPoint,circleCordinates[2]) == false) {
           return ;
