       int centerXPoint = circleCordinates[0];
       int tempLeftPoint = tempXPoint-1;
       int tempRightPoint = tempXPoint;
       int centerYPoint = circleCordinates[1];
       int radius = circleCordinates[2];
       //for left
       while(isPointInsideTheCircle(centerXPoint,tempLeftPoint,centerYPoint,tempYPoint,radius)) {
           int key = tempLeftPoint * 1000000 + tempYPoint;
           uniquePoints.insert(key);
           tempLeftPoint --;
       }
       //for right
       while(isPointInsideTheCircle(centerXPoint,tempRightPoint,centerYPoint,tempYPoint,radius)) {
            int key = tempRightPoint * 1000000 + tempYPoint;
           uniquePoints.insert(key);
           tempRightPoint ++;
       }
       //for up
       helper(circleCordinates,tempXPoint,tempYPoint+1, tempYPoint+1,upLimit);
       //for down
