class Solution {
    bool inCircle(const vector<int> &circ, int x, int y) {
        int dx = (x - circ[0]) * (x - circ[0]); 
        int dy = (y - circ[1]) * (y - circ[1]);
        return dx + dy <= circ[2] * circ[2];
    }
