var countLatticePoints = function(circles) {
      const points = [];
      const usedCircles = [];
      circles.forEach(c => {
          const [xc,yc,rc] = c;
          if(usedCircles.includes(`(${xc},${yc},${rc})`)) return;
          usedCircles.push(`(${xc},${yc},${rc})`)
          for(let x=xc-rc;x<=xc+rc;x++){
             for(let y = yc-rc;y<=yc+rc;y++){
              const cdata = Math.pow((x-xc),2)+Math.pow((y-yc),2);
              const rp = Math.pow(rc,2);
              if(cdata <= rp) points.push(`(${x},${y})`)
            } 
          }
      })
      return [...new Set(points)].length
};
