impl Solution {
    pub fn count_lattice_points(c: Vec<Vec<i32>>) -> i32 {
        let mut m=[0;40401];
        for v in c.into_iter() {
            let (x,y,r) = (v[0],v[1],v[2]);
            for i in x-r..x+r+1 {
                let d=((r*r-(x-i)*(x-i)) as f64).sqrt() as i32; 
                for j in i*201+y-d..i*201+y+d+1 {m[j as usize]=1;}
            }    
        }
        m.iter().sum::<i32>()
    }
}
