var countLatticePoints = function(circles) {
    const coordinateRange = Array.from({ length: 201 }, () => []);

    const isInsideCircle = (j, x, i, y, r) => (j - x) ** 2 + (i - y) ** 2 <= r * r;

	/*   Time O(radius * circles)   */
    for (const [x, y, r] of circles) {
        for (let i = y + r, j = x; i >= y; i--) {
            for (; isInsideCircle(j, x, i, y, r); j++);

            coordinateRange[i].push([2 * x - (j - 1), j - 1]);
            coordinateRange[2 * y - i].push([2 * x - (j - 1), j - 1]);
        }
    }

	/*   Time O(Y * circles * log(circles))  */
    coordinateRange.forEach((ranges) =>
        ranges.sort(([start2, end2], [start1, end1]) =>
            start2 === start1 ? end2 - end1 : start2 - start1
        )
    );

	let ans = 0;

    for (const ranges of coordinateRange) {
        let start = 0, end = -1;

        for (const range of ranges) {
            if (range[0] > end) {
                ans += end - start + 1;
                start = range[0];
            }
            end = Math.max(end, range[1]);
        }
        ans += end - start + 1;
    }

    return ans;
};
