class Solution:
    def countLatticePoints(self, circles: List[List[int]]) -> int:

        #Will be used to ignore duplicate circles
        used = set();

        #Put all the lattice points in a set
        latticePoints = set();

        for triplet in circles:
            circle_id = str( triplet );

            #To ignore duplicates
            if circle_id in used:
                continue;

            #Feed through helper
            self.addLP( latticePoints, triplet );
            used.add( circle_id );

        #Return the length of the set
        return len( latticePoints );

    def addLP( self, latticePoints, triplet ):

        #Unpack values
        r = triplet[ 2 ];
        d = r * 2;
        x =  triplet[ 0 ] - r;
        y =  triplet[ 1 ] - r;

        #Used_lengths keeps track of good lengths
        valid_lengths = set();
        invalid_lengths = set();

        for i in range( d + 1 ):
            for j in range( d + 1 ):

                #Find the vertical and horizontal length
                #Relative to the center of the circle
                #To the current coorindate ( a,b )
                a = x + i;
                length_a = abs( triplet[ 0 ] - a);
                b = y + j;
                length_b = abs( triplet[ 1 ] - b);


                length_id = str( [ length_a, length_b ] );

                #If this length combo has been evaluated:
                if length_id in valid_lengths:
                    latticePoints.add( str( [ x + i, y + j ]) );
                elif length_id in invalid_lengths:
                    continue;

                #Otherwise, check it and add to relevant set
                else:
                    is_inside = self.inside( length_a, length_b, r );
                    if is_inside:
                        latticePoints.add( str( [ x + i, y + j ]) );
                        valid_lengths.add( length_id );
                    else:
                        invalid_lengths.add( length_id );

    #a^2 + b^2 = c^2  ==>
    #c = sqrt( a^2 + b^2 )
    #Check to make sure c is less than or equal to r
    def inside( self, length_a , length_b, r ):
        a_square = math.pow( length_a, 2 );
        b_square = math.pow( length_b, 2 );
        c = math.sqrt( a_square + b_square );
        return c <= r;

