    def countLatticePoints(self, circles: List[List[int]]) -> int:
        radius_inclusion_map = {<PRECALCULATED_RESULTS>}
        included = set()
        for circle in circles:
            for x in range(circle[0] - circle[2], circle[0] + circle[2] + 1):
                for y in range(circle[1] - circle[2], circle[1] + circle[2] + 1):
                    if (x, y) in included:
                        continue
                    diff_1, diff_2 = abs(x - circle[0]), abs(y - circle[1])
                    long_pole, short_pole = (diff_1, diff_2) if diff_1 >= diff_2 else (diff_2, diff_1)
                    if long_pole <= circle[2]//2 or short_pole <= radius_inclusion_map[circle[2]][long_pole]:
                        included.add((x, y))
        return len(included)
